#import "ViewController.h"
#import <sys/sysctl.h>
#import <mach/mach.h>

@interface ViewController ()

@end

@implementation ViewController

- (void)viewDidLoad {
    [super viewDidLoad];
    // Do any additional setup after loading the view, typically from a nib.
    
    double totalDisk = [[self totalDiskSpace] floatValue] / 1024.0 / 1024.0;
    double freeDisk = [[self freeDiskSpace] floatValue] / 1024.0 / 1024.0;
    
    NSLog(@"totalDisk : %f, freeDisk : %f",totalDisk, freeDisk);
    
    int 变量 = 2;
    NSLog(@"变量 : %d",变量);
    
    // IAP 本地验证
    NSString *receiptStr = @"";
    [self validateReceipt: receiptStr];
    
}

- (void) validateReceipt:(NSString *)theReceipt{
    NSLog(@"validateReceipt ....");
    
    NSString *verify_real = @"https://buy.itunes.apple.com/verifyReceipt";
    NSString *verify_sandbox = @"https://sandbox.itunes.apple.com/verifyReceipt";
    
    NSURL *storeURL = [NSURL URLWithString:verify_sandbox];
    
    NSString *jsonOut = [NSString stringWithFormat:@"{\"receipt-data\":\"%@\"}", theReceipt];
    NSLog(@"jsonOut : %@", jsonOut);
    
    //create the request
    NSMutableURLRequest *theRequest = [NSMutableURLRequest requestWithURL:storeURL
                                                              cachePolicy:NSURLRequestReloadIgnoringLocalAndRemoteCacheData
                                                          timeoutInterval:15.0];
    
    [theRequest setHTTPMethod:@"POST"];
    [theRequest setHTTPBody:[jsonOut dataUsingEncoding:NSUTF8StringEncoding]];
    
    NSData *result = [NSURLConnection sendSynchronousRequest:theRequest returningResponse:nil error:nil];
    
    if(result == nil){
        NSLog(@"验证失败");
    }else{
        NSDictionary *dict = [NSJSONSerialization JSONObjectWithData:result options:NSJSONReadingAllowFragments error:nil];
        NSLog(@"验证结果 : %@",dict);
        
        if(dict != nil){
            NSLog(@"验证成功");
        }

    }
}

- (void)didReceiveMemoryWarning {
    [super didReceiveMemoryWarning];
    // Dispose of any resources that can be recreated.
}


-(double) availableMemory{
    vm_statistics_data_t vmStats;
    mach_msg_type_number_t infoCount = HOST_VM_INFO_COUNT;
    kern_return_t kernReturn = host_statistics(mach_host_self(), HOST_VM_INFO, (host_info_t) &vmStats, &infoCount);
    
    if(kernReturn != KERN_SUCCESS){
        return NSNotFound;
    }
    
    return ((vm_page_size * vmStats.free_count) / 1024.0) / 1024.0;
}

- (double)usedMemory{
    task_basic_info_data_t taskInfo;
    mach_msg_type_number_t infoCount = TASK_BASIC_INFO_COUNT;
    kern_return_t kernReturn = task_info(mach_task_self(),
                                         TASK_BASIC_INFO,
                                         (task_info_t)&taskInfo,
                                         &infoCount);
    
    if(kernReturn != KERN_SUCCESS){
        return NSNotFound;
    }
    
    return taskInfo.resident_size / 1024.0 / 1024.0;
}

- (NSNumber *)totalDiskSpace{
    NSDictionary *fattributes = [[NSFileManager defaultManager] fileSystemAttributesAtPath:NSHomeDirectory()];
    
    return [fattributes objectForKey:NSFileSystemSize];
}

- (NSNumber *)freeDiskSpace{
    NSDictionary *fattributes = [[NSFileManager defaultManager] fileSystemAttributesAtPath:NSHomeDirectory()];
    return [fattributes objectForKey:NSFileSystemFreeSize];
}


@end
